function ecpt(s,p,n){
    var fxn=n?n:3;
	var len=s.length;
	p=p?strCharactCode(p):p;
	var r="";
	for(var i=0,j=0;i<len;i++,j++){
		if(p&&j==p.length)j=0;
		var itm=s.charCodeAt(i)+(p?p.charCodeAt(j):0);
		r+=pfxChar(charVal(itm),fxn);
	}
	return r;
}
function dcpt(s,p,n){
    var fxn=n?n:3;
	var len=s.length;
	p=p?strCharactCode(p):p;
	var r="";
	for(var i=0,j=0;i<len;i+=fxn,j++){
		if(p&&j==p.length)j=0;
		var itm=s.substring(i,i+fxn);
		r+=String.fromCharCode(numVal(itm)-(p?p.charCodeAt(j):0));
	}
	return r;
}
function charVal(o){
	var a = Math.floor(o/62);
	var b = o%62;
	var c = String.fromCharCode(b<10?b+48:(b>35?b+61:b+55));
	var r = "";
	if (a > 0)
		r=charVal(a);
	return r + c;
}
function numVal(s,t){
	if(!t)t=0;
	if(t>s.length-1)return 0;
	var a=s.charCodeAt(s.length-t-1);
	var b=a<65?a-48:(a<97?a-55:a-61);
	return b*Math.pow(62,t)+numVal(s,t+1);
}
function pfxChar(s,l){
	if(s.length<l)return pfxChar("0"+s,l);
	return s;
}
function strCharact(s,i){
    var j=i%17;
    if(i<s.length)return s.charCodeAt(i)*Math.pow(62,j>0?j:19)+strCharact(s,i+1)+i;
    return s.length;
}
function strCharactCode(s){
	var r=charVal(strCharact(s,0));
	return r.split("").reverse().join("");
}

function aesEcpt(text,pwd){
	var shp=sha256_digest(pwd);
	var key = [];
	var iv = [];
	for(var i=16;i<48;i++){
	    var itm=shp.charCodeAt(i);
	    if(i<32)key.push(itm);
		else iv.push(itm);
	}
	var textBytes = aesjs.utils.utf8.toBytes(text);
	var aesOfb = new aesjs.ModeOfOperation.ofb(key, iv);
	var encryptedBytes = aesOfb.encrypt(textBytes);
	var encryptedHex = aesjs.utils.hex.fromBytes(encryptedBytes);
	return encryptedHex;
}
function aesDcpt(text,pwd){
	var shp=sha256_digest(pwd);
	var key = [];
	var iv = [];
	for(var i=16;i<48;i++){
	    var itm=shp.charCodeAt(i);
	    if(i<32)key.push(itm);
		else iv.push(itm);
	}
	var encryptedBytes = aesjs.utils.hex.toBytes(text);
	var aesOfb = new aesjs.ModeOfOperation.ofb(key, iv);
	var decryptedBytes = aesOfb.decrypt(encryptedBytes);
	var decryptedText = aesjs.utils.utf8.fromBytes(decryptedBytes);
	return decryptedText;
}